import type { Hex } from "viem"
import { describe, expect, test } from "vitest"

import type { HappyTx } from "../../types/happyTx"
import { encode } from "../../lib/happyTxLib"

// Test cases for encoding
describe("encode", () => {
    test("should correctly encode tx with empty dynamic data", () => {
        const tx: HappyTx = {
            account: "0x1234567890123456789012345678901234567890",
            gasLimit: 1000000n,
            executeGasLimit: 800000n,
            dest: "0x2345678901234567890123456789012345678901",
            paymaster: "0x3456789012345678901234567890123456789012",
            value: 1000000000000000000n, // 1 ETH (10^19 in wei)
            nonce: 1n,
            maxFeePerGas: 2000000000n, // (2 * 10^9) wei
            submitterFee: 100000000n,  // (10^8) wei
            callData: "0x1234",
            paymasterData: "0x",
            validatorData: "0x",
            extraData: "0x",
        }

        const expected: Hex = ""
        const encoded = encode(tx)
        expect(encoded).toBe(expected)
    })

    test("should correctly encode tx with only extraData empty", () => {
        const tx: HappyTx = {
            account: "0x1234567890123456789012345678901234567890",
            gasLimit: 1000000n,
            executeGasLimit: 800000n,
            dest: "0x2345678901234567890123456789012345678901",
            paymaster: "0x3456789012345678901234567890123456789012",
            value: 1000000000000000000n,
            nonce: 1n,
            maxFeePerGas: 2000000000n,
            submitterFee: 100000000n,
            callData: "0x1234",
            paymasterData: "0x5678",
            validatorData: "0x9abc",
            extraData: "0x",
        }

        const expected: Hex = ""
        const encoded = encode(tx)
        expect(encoded).toBe(expected)
    })

    test("should correctly encode tx with all fields", () => {
        const tx: HappyTx = {
            account: "0x1234567890123456789012345678901234567890",
            gasLimit: 1000000n,
            executeGasLimit: 800000n,
            dest: "0x2345678901234567890123456789012345678901",
            paymaster: "0x3456789012345678901234567890123456789012",
            value: 1000000000000000000n,
            nonce: 1n,
            maxFeePerGas: 2000000000n,
            submitterFee: 100000000n,
            callData: "0x1234",
            paymasterData: "0x5678",
            validatorData: "0x9abc",
            extraData: "0xdef0",
        }

        const expected: Hex = ""
        const encoded = encode(tx)
        expect(encoded).toBe(expected)
    })

    test("should correctly encode tx with big dynamic fields", () => {
        const tx: HappyTx = {
            account: "0x1234567890123456789012345678901234567890",
            gasLimit: 1000000n,
            executeGasLimit: 800000n,
            dest: "0x2345678901234567890123456789012345678901",
            paymaster: "0x3456789012345678901234567890123456789012",
            value: 1000000000000000000n,
            nonce: 1n,
            maxFeePerGas: 2000000000n,
            submitterFee: 100000000n,
            callData: "0xabcdef123456abcdef123456abcdef123456abcdef123456abcdef123456abcdef123456abcdef123456abcdef123456",
            paymasterData: "0x789078907890789078907890789078907890789078907890789078907890789078907890789078907890789078907890",
            validatorData: "0x9abc",
            extraData: "0xdef0def0def0def0def0def0def0def0def0def0def0def0def0def0def0def0def0",
        }

        const expected: Hex = ""
        const encoded = encode(tx)
        expect(encoded).toBe(expected)
    })
})